import React from 'react'
import './ProductTemplate.css'

import '../Details.js'

function ProductTemplate(props) {
    return (
        <div className='center'>
            {
                details.map((item) => {

                    const { id, name, description, image } = item

                    const path = props.props.location.pathname

                    console.log(path);

                    if (path == `/products${id}`) {
                        return (
                            <div className='product'>
                                <div>
                                    <img
                                        alt="1"
                                        src={image}
                                        className="template__image" />
                                </div>


                                <div>
                                    <h1>{name}</h1>
                                    <h1>{description}</h1>
                                </div>
                            </div>
                )

                    }


            })

          }


        </div>
    );
}

export default ProductTemplate
