import React from 'react'
import './ProductTemplate.css'

import '../Details.js'
import { Link } from 'react-router-dom';

function ProductTemplate(props) {
    return (
        <div className='center container'>
            {
                details.map((item) => {

                    const { id, name, description, image } = item

                    const path = props.props.location.pathname

                    console.log(path);

                    if (path == `/products${id}`) {
                        return (
                            <div className="template__main">
                                <div className='product'>
                                    <div className='template__imageDiv center'>
                                        <img
                                            alt="1"
                                            src={image}
                                            className="template__image" />
                                    </div>


                                    <div>
                                        <h1 className='template__name'>{name}</h1>
                                        <h1 className='template__description'>{description}</h1>
                                    </div>
                                </div>

                                <div className='center'>
                                    <Link className='template__btn center' to='/contact'>Get Quote</Link>
                                </div>
                                </div>
                )

                    }

                    else {
                        }


            })

          }


        </div>
    );
}

export default ProductTemplate
