import React from 'react'
import './ProductBox'

import '../../Details.js'
import ProductBox from './ProductBox';


const HomeProductBaseUrl = "../../assets/products/";

function Products() {
    return (
        <div>
            {details.map((product) => {

                const {id, name, description, img} = product;
                return (
                    <ProductBox
                        key={product.id}
                        name={product.name}
                        description={ product.description}
                        image={`${HomeProductBaseUrl}${img}`}
                    />

              )  
            })
            }
        </div>
    );
}

export default Products
